@-webkit-keyframes shadowPulse {
    0% {
        -webkit-box-shadow: 0 0 0 0 var(--box-shadow-color);
    }
    70% {
        -webkit-box-shadow: 0 0 0 10px var(--box-shadow-color-opacity);
