    0% {
        -moz-box-shadow: 0 0 0 0 var(--box-shadow-color);
        box-shadow: 0 0 0 0 var(--box-shadow-color);
    }
    70% {
        -moz-box-shadow: 0 0 0 10px var(--box-shadow-color-opacity);
        box-shadow: 0 0 0 10px var(--box-shadow-color-opacity);
